## This script creates Fig 9import pandas as pdimport matplotlib.pyplot as pltdf = pd.read_csv('data_raw/county_1999_2024_myCrude.csv')# Rename the columns for consistencydf.rename(columns={"Year Code": "Year"}, inplace=True)# Define the counties of interest and their display namescounty_mapping = {    "Shannon County, SD": "Oglala Lakota County, SD",    "McKinley County, NM": "McKinley County, NM",    "Apache County, AZ": "Apache County, AZ",    "Navajo County, AZ": "Navajo County, AZ",    "Rio Arriba County, NM": "Rio Arriba County, NM",    "Los Angeles County, CA": "Los Angeles County, CA"}# Filter the data for selected countiesfiltered_df = df[df["County"].isin(county_mapping.keys())]# Pivot the data for plottingpivot_df = filtered_df.pivot(index="Year", columns="County", values="Crude Rate")# Plot the crude ratesplt.figure(figsize=(7, 6), dpi=400, tight_layout=True)for county, label in county_mapping.items():    if county in pivot_df.columns:        plt.plot(pivot_df.index, pivot_df[county], marker='o', label=label)plt.ylim(-10, 360)  # Setting the y-axis limitplt.yticks(range(0, 351, 50), fontsize=12)  # Major ticks every 50plt.minorticks_on()plt.gca().yaxis.set_minor_locator(plt.MultipleLocator(10))  # Minor ticks every 10plt.xlabel("Year", fontsize=12)plt.ylabel("Crude Rate", fontsize=12)plt.legend(frameon=False, fontsize=12)plt.grid(True, alpha=0.2)plt.savefig('Fig9.tiff', dpi=400, format="tiff", pil_kwargs={"compression": "tiff_lzw"})